/* eslint-disable */
import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { AllyControl, AllyContainer } from './styled';
import PropTypes from "prop-types";

class A11yTest extends Component {
  constructor(props) {
    super(props);
    this.customFields = props.customFields;
    this.state = {
      isToggleOn: false,
      errorReport: null
    };
    this.axeCore = null;
    this.axe = null;
    if (typeof window !== 'undefined') {
      try {
        this.axeCore = require('axe-core');
        this.axe  = require('./axe');

        if(this.customFields.showUI){
          window.addEventListener('a11yAlerts', this.getAccessibilityErrors);
        }
        this.axe(React, ReactDOM, 5000);
        let levelRules = [];
        if(this.customFields.level === 'a' || this.customFields.level === 'aa'){
          levelRules.push('wcag2a');
          levelRules.push('wcag21a');
        }
        if(this.customFields.level === 'aa'){
          levelRules.push('wcag2aa');
          levelRules.push('wcag21aa');
        }
        if(this.customFields.includeBestPractices){
          levelRules.push('best-practice');
        }
        const config = this.axeCore.getRules(levelRules).map((rule) => ({
          ...rule,
          id: rule.ruleId,
          enabled: true,
        }));
        const context = {
          include: [['#fusion-app']],
          exclude: [['#a11yContainer'], ['#a11y-trigger']],
        };
        this.axe(React, ReactDOM, 6500, {
          rules: config,
          disableOtherRules: true,
          iframes: false,
        }, context);
      } catch (e) {
        console.error('Error in running axe: ', e);
      }
    }
  }

  componentDidMount() {
    if(this.customFields.showUI){
      this.dynamicContentElement.addEventListener("click", this.handleElementButtonClick);
    }
  }

  getAccessibilityErrors = (report) => {
    this.setState(state => ({
      isToggleOn: this.state.isToggleOn,
      errorReport: report.detail
    }));
  };

  handlePanelClick = () => {
    let temp = true;
    if(this.state.isToggleOn === true){
      document.getElementById("fusion-app").style.left = "0";
      temp = false;
    }else {
      document.getElementById("fusion-app").style.left = "575px";
      temp = true;
    }
    this.setState(state => ({
      isToggleOn: temp,
      errorReport: state.errorReport
    }));
  };

  handleElementButtonClick = (e) => {
    if (e.target.classList.contains('allyIdBtn')) {
      let button = e.target;
      let el = document.querySelector(button.dataset.select);
      if(el){
        if(el.classList.contains('a11yHighlight')){
          el.classList.remove('a11yHighlight');
          button.innerHTML = "Highlight Element";
        }else{
          el.classList.add('a11yHighlight');
          button.innerHTML = "Remove Highlight"
        }
      }
    }
  };

  createMarkup = () => {
    if(this.state.errorReport !== null){
      return {__html: this.state.errorReport};
    }else {
      return {__html: `
        <div class="reportContainer">
            <h1>Currently no errors</h1>
            <p>Note: If you just added this control in PageBuilder, you will need to refresh the page to run the audit</p>
        </div>
      `};
    }
  };

  componentWillUnmount() {
    if(this.customFields.showUI){
      window.removeEventListener('a11yAlerts', this.getAccessibilityErrors);
      this.dynamicContentElement.addEventListener("click", this.handleElementButtonClick);
    }
  }

  render() {
    return this.customFields.showUI ? (
      <>
        <style dangerouslySetInnerHTML={{__html: `
          .a11yHighlight {
            border: 4px solid red !important;
            box-shadow: 0 0 0 0 rgba(255, 82, 82, 1);
            animation: pulse-red 2s infinite;
          }  
          
          @keyframes pulse-red {
            0% {
              transform: scale(0.95);
              box-shadow: 0 0 0 0 rgba(255, 82, 82, 0.7);
            }
            70% {
              transform: scale(1);
              box-shadow: 0 0 0 10px rgba(255, 82, 82, 0);
            }
            100% {
              transform: scale(0.95);
              box-shadow: 0 0 0 0 rgba(255, 82, 82, 0);
            }
          }          
        `}} />
          <AllyControl
              className={` ${this.customFields.panelTriggerControl ? this.customFields.panelTriggerControl : 'topright'}`}
              aria-expanded="false" id="a11y-trigger" onClick={this.handlePanelClick}>
              <img src="data:image/png;base64,"
                   alt="Show accessible error panel" />
          </AllyControl>

          <AllyContainer id="a11yContainer"
               ref={el => this.dynamicContentElement = el}
               className={`allyContainer ${this.state.isToggleOn ? 'ally-panel-active' : ''}`}>
            <div className="header">
              <h1>A11Y Issues </h1>
              <button className="right" onClick={this.handlePanelClick}>Close Panel</button>
            </div>

            <div className="alert">
              <h2>Note: this is for diagnosing acessibility concerns on local and staging environments only.
                Do not add this feature to a production page as it will degrade performance significantly.</h2>
            </div>

            <section className="allyInnerContainer"
                     dangerouslySetInnerHTML={this.createMarkup()} />
          </AllyContainer>
        </>
    ) : null;
  }
}

A11yTest.propTypes = {
  customFields: PropTypes.shape({
    showUI: PropTypes.bool.tag({
      label: 'Show A11y UI',
      defaultValue: false,
      description: 'In addition to console logging, the UI provides an interactive component on your page. Requires page refresh after change.'
    }),
    panelTriggerControl: PropTypes.oneOf(['topleft', 'topright', 'bottomleft', 'bottomright']).tag({
      defaultValue: 'topright',
      labels: { topleft: 'Top Left Corner', topright: 'Top Right Corner', bottomleft: 'Bottom Left Corner', bottomright: 'Bottom Right Corner'},
      name: 'UI Panel Trigger Control',
      description: 'Select where on the page to place the button control that open and closes the a11y panel.'
    }),
    level: PropTypes.oneOf(['a', 'aa']).tag({
      defaultValue: 'aa',
      labels: { a: 'Level A', aa: 'Level AA'},
      name: 'WCAG A11y Levels',
      description: 'Select the level of accessibility you need to target. Requires page refresh after change.'
    }),
    includeBestPractices: PropTypes.bool.tag({
      label: 'Include additional best practices',
      defaultValue: true,
      description: 'Provides further insight into common a11y best practices.  Requires page refresh after change.'
    }),
  }),
};

A11yTest.label = 'A11y Test – Arc Block';

export default A11yTest;
